class PpLog extends BasePpLog {

    /**
     * Static function for saving log
     * @param PpPlugin $plugin
     * @param string $user
     * @param string $log
     */
    public static function logMe($plugin, $user, $log) {
        $l = new PpLog();
        if ($plugin instanceof PpPlugin) {
            $l->pluginid = $plugin->publicid;
            $l->plugin_name = $plugin->plugin_name;
        } else {
            $l->pluginid = '0';
            $l->plugin_name = 'n/a';
        }
        $l->stamp = date('Y-m-d H:i:s');
        $l->userid = $user;
        $l->log = $log;
        $l->save();
    }

}